CREATE DATABASE SIP;
USE SIP;

CREATE TABLE HotelLogin (
    loginID INT NOT NULL IDENTITY(10000001, 1) PRIMARY KEY,
    loginName NVARCHAR(100) NOT NULL,
    email NVARCHAR(100) NOT NULL UNIQUE,
    password NVARCHAR(100) NOT NULL,
    headshot VARBINARY(MAX),
    googleID VARCHAR(30),
    lineID  VARCHAR(40)
);

CREATE TABLE Hotel (
    hotelID INT NOT NULL IDENTITY(10000001, 1) PRIMARY KEY,
    loginID INT NOT NULL,
    hotelName NVARCHAR(100) NOT NULL,
    hotelType NVARCHAR(50) NOT NULL,
    hotelStar NVARCHAR(10) NOT NULL,
    tel NVARCHAR(20) NOT NULL,
    country NVARCHAR(50) NOT NULL,
    city NVARCHAR(50) NOT NULL,
    region NVARCHAR(50) NOT NULL,
    street NVARCHAR(100),
    postalCode NVARCHAR(20) NOT NULL,
	hotelIntroduction NVARCHAR(MAX),
    state BIT NOT NULL,
    FOREIGN KEY (loginID) REFERENCES HotelLogin(loginID)
);

CREATE TABLE HotelDetail (
    hotelID INT NOT NULL PRIMARY KEY,
    mapURL NVARCHAR(100) NOT NULL,
    guiNumber NVARCHAR(40) NOT NULL,
    businessName NVARCHAR(100) NOT NULL,
    openYear INT NOT NULL,
    cleaningService BIT ,
    expressCheckin BIT ,
    counter24hr BIT ,
    freeWiFi BIT ,
    roomCard BIT ,
    noSmoking BIT , 
    petFriendly BIT ,
    petDeposit INT ,
    petCleaningFee INT ,
    reservationNotice NVARCHAR(1000),
    FOREIGN KEY (hotelID) REFERENCES Hotel(hotelID)
);

CREATE TABLE Room(
	hotelID INT NOT NULL ,
    productID VARCHAR(36) NOT NULL ,
    productName NVARCHAR(100) NOT NULL,
    productType VARCHAR(30) NOT NULL,	/* 房間類型:單?雙?套房? */
    price INT NOT NULL,
    state BIT NOT NULL,	/* 狀態 */
    capacity INT NOT NULL,	/* 房間最多容納人數 */
    productDescription NVARCHAR(max) ,
    score INT ,
    checkInTime char(5) NOT NULL,
    checkOutTime char(5) NOT NULL,
    childExtraBed BIT ,
    childrenPrice INT ,	/* childExtraBed 的價格 */
    age INT , 	/* childExtraBed 的使用限制年齡 */
    privateBathroom BIT NOT NULL ,
    showerRoom BIT NOT NULL,
	createDate DATETIME DEFAULT CURRENT_TIMESTAMP,  /*因為uuid排序問題 改用創建日期做排序，預設為當前時間 */
    PRIMARY KEY (productID),
	UNIQUE (hotelID, productID),
    FOREIGN KEY (hotelID) REFERENCES Hotel(hotelID)
);
CREATE TABLE RoomQuantityPriceByDate (
    roomQuantityID VARCHAR(36) PRIMARY KEY,
    productID VARCHAR(36) NOT NULL,
    quantityByDate INT,
	price INT NOT NULL,
    date DATE,
	discountPrice INT ,
    FOREIGN KEY (productID) REFERENCES Room(productID)
);
CREATE TABLE RoomPicture (
    imgID INT IDENTITY(10000001, 1) NOT NULL,
    productID VARCHAR(36) NOT NULL ,
    imageData VARBINARY(MAX) NOT NULL,
    PRIMARY KEY (imgID),
    FOREIGN KEY (productID) REFERENCES Room(productID)
);
CREATE TABLE HotelLoginPicture (
    HotelLoginImgID INT IDENTITY(10000001, 1) NOT NULL,
    loginID INT NOT NULL ,
    imageData VARBINARY(MAX) NOT NULL,
    PRIMARY KEY (HotelLoginImgID),
    FOREIGN KEY (loginID) REFERENCES HotelLogin(loginID)
);

CREATE TABLE DefaultPicture (
	defaultPictureID INT IDENTITY(10000001, 1) NOT NULL,
    PictureName NVARCHAR(100) NOT NULL ,
    imageData VARBINARY(MAX) NOT NULL,
    PRIMARY KEY (DefaultPictureID)
);

CREATE TABLE Customer (
    loginID INT NOT NULL IDENTITY(10000001, 1) PRIMARY KEY,
    email NVARCHAR(100) NOT NULL UNIQUE,
    password NVARCHAR(100) NOT NULL,
	CustomerName NVARCHAR(100) NOT NULL,
    sex NVARCHAR(10) NOT NULL,
    birthday DATE NOT NULL,
    phone NVARCHAR(20) NOT NULL,
    headshot VARBINARY(MAX),
    country NVARCHAR(50) NOT NULL,
    city NVARCHAR(50) NOT NULL,
    region NVARCHAR(50) NOT NULL,
    street NVARCHAR(100),
    postalCode NVARCHAR(20) NOT NULL,
	googleID VARCHAR(30),
	LineID  VARCHAR(40)
);

CREATE TABLE OrderTable (
    orderID VARCHAR(36) NOT NULL PRIMARY KEY,
    loginID INT NOT NULL,
    hotelID INT NOT NULL,
	email NVARCHAR(100) NOT NULL,
	lastName NVARCHAR(40) NOT NULL, 
	firstName NVARCHAR(40) NOT NULL, 
	tel NVARCHAR(20) NOT NULL,
	orderStatus NVARCHAR(20) NOT NULL DEFAULT 'valid',
    orderTime NVARCHAR(40) NOT NULL,
    updateTime NVARCHAR(40) NOT NULL,
	orderPrice INT NOT NULL,
    payment NVARCHAR(40) NOT NULL,
    numPeople INT NOT NULL,  
    checkInDate DATE NOT NULL,
    checkOutDate DATE NOT NULL, 
    evaluation NVARCHAR(40), 
    comment NVARCHAR(40),
    FOREIGN KEY (loginID) REFERENCES Customer(loginID),
    FOREIGN KEY (hotelID) REFERENCES Hotel(hotelID),
);

CREATE TABLE OrderItem (
    orderItemID VARCHAR(36) NOT NULL PRIMARY KEY,
    orderID VARCHAR(36) NOT NULL,
    productID VARCHAR(36) NOT NULL,
    quantity INT NOT NULL,
	orderItemPrice INT NOT NULL,
    FOREIGN KEY (orderID) REFERENCES OrderTable(orderID),
    FOREIGN KEY (productID) REFERENCES Room(productID)
);

